import ExpenseItem from './ExpenseItem';
import './NewComponentExpenses.css';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import React, { useState } from 'react';

const NewComponentExpenses = (props) => {


    const[filteredYear, setFilteredYear] = useState('2021');



    const selectedNewYear = (selectedYear) =>
    {
        setFilteredYear(selectedYear.target.value);

        console.log("Aye the user selected the year " + selectedYear.target.value);
    }


    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter selected={filteredYear} onSelectedNewYear={selectedNewYear}/> 
                <ExpenseItem 
                    title={props.items[0].title}
                    amount={props.items[0].amount}
                    date={props.items[0].date}>
                </ExpenseItem>
                <ExpenseItem 
                    title={props.items[1].title}
                    amount={props.items[1].amount}
                    date={props.items[1].date}>
                </ExpenseItem>
                <ExpenseItem 
                    title={props.items[2].title} 
                    amount={props.items[2].amount} 
                    date={props.items[2].date}>
                </ExpenseItem>
                <ExpenseItem 
                    title={props.items[3].title} 
                    amount={props.items[3].amount} 
                    date={props.items[3].date}>
                </ExpenseItem>
            </Card>
        </div>
    );
}
export default NewComponentExpenses;
