import ExpenseItem from './ExpenseItem';
import './NewComponentExpenses.css';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import React, { useState } from 'react';

const NewComponentExpenses = (props) => {

    const[filteredYear, setFilteredYear] = useState('2021');

    const filteredArray = [];

    const selectedNewYear = (selectedYear) =>
    {
        setFilteredYear(selectedYear);

        //console.log("The year chosen = " + selectedYear);

        /* traverse through array from parent and add it to filteredArray */
        for(let i = 0; i < props.items.length; i++)
        {
            /* get current year of current index */
            let curYear = props.items[i].date.getFullYear();

            /* convert years into number to compare with one another */
            if((Number(curYear) === Number(selectedYear)))
            {
                filteredArray.push(props.items[i]); // push to filteredArray
            } 

        }

        for(let j = 0; j < filteredArray.length; j++)
        {
            console.log("(key=" + filteredArray[j].key + ", title="+ filteredArray[j].title + ", amount=" + filteredArray[j].amount + ", date=" + filteredArray[j].date.getFullYear() + ")");

        } 




    }

    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter selected={filteredYear} onSelectedNewYear={selectedNewYear}/> 
                {props.items.map((expense) => (
                    <ExpenseItem 
                        key = {expense.id}
                        title = {expense.title} 
                        amount = {expense.amount}
                        date = {expense.date}
                />))}
            </Card>
        </div>
    );
}
export default NewComponentExpenses;
