import ExpenseItem from './ExpenseItem';
import './NewComponentExpenses.css';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import React, { useState } from 'react';

const NewComponentExpenses = (props) => {

    const[filteredYear, setFilteredYear] = useState('2021');

    const filteredArray = [];

    const selectedNewYear = (selectedYear) =>
    {
        setFilteredYear(selectedYear);

        //console.log("The year chosen = " + selectedYear);

        /* traverse through array from parent and add it to filteredArray */
        for(let i = 0; i < props.items.length; i++)
        {
            curYear = props.items[i].date.getFullYear();
            if(curYear === selectedYear)
            {
                filteredArray.push(props.items[i]);
            } 

            console.log("(key=" + props.items[i].key + ", title="+ props.items[i].title + ", amount=" + props.items[i].amount + ", date=" + props.items[i].date.getFullYear());
        }

        console.log("props.items[0].year " + props.items[0].date.getFullYear());
        console.log("props.items[0].year is 2021? " + (props.items[0].date.getFullYear()===2021));
        console.log("props.items[2].year is 2021? " + (props.items[2].date.getFullYear()===2021));


    }

    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter selected={filteredYear} onSelectedNewYear={selectedNewYear}/> 
                {props.items.map((expense) => (
                    <ExpenseItem 
                        key = {expense.id}
                        title = {expense.title} 
                        amount = {expense.amount}
                        date = {expense.date}
                />))}
            </Card>
        </div>
    );
}
export default NewComponentExpenses;
